import React,{ useState } from 'react';
import './App.css';
import logo from "./assets/logo2.png"
import api from './services/api';
 import Routes from './routes'

function App() {
  const[email,setEmail] =  useState('');
  const[password,setPassword] = useState('')   

  async function handleSubmit(event){
    event.preventDefault();
    console.log(email);
    console.log(password);
    // const response = await api.post('/sessions',{email})
    // const{_id} = response.data;
    // localStorage.setItem('user',_id);

  }

  return (
    <div className="container">
      <div className="colorcompletetop"></div>
      <div className="tittlebar">
        <img src={logo} alt="" className="logo"/>
        <h2 className="tittle">DARK SOULS</h2>
        <img src={logo} alt="" className="logo"/>
      </div>
      <div className="colorcompletebot"></div>
      <div className="content">
        <p className="textlogin">Ofere√ßa <strong>mortes</strong> para jogadores e encontre <strong>bonfires</strong> para o seu save</p>


        <form onSubmit={handleSubmit}>
          <label htmlFor="email">E-mail</label>
          <input 
            type="email" 
            id="email" 
            placeholder="Tente E-mail"
            value={email}
            onChange= {event=> setEmail(event.target.value)}>
          </input>
          <label htmlFor="password">Senha</label>
          <input 
            type="password" 
            id="password" 
            placeholder="Tente Senha"
            value={password}
            onChange= {event=> setPassword(event.target.value)}>
          </input>
          <button className="btn" type="submit">Entrar</button>  
        </form>  
      </div>
    </div>

  );
}

export default App;
